const StatusOn = 1 //开启

//GetAdmin
/**
 * @desc：获取管理员
 * @param siteId
 * @return res
 * @return err
 * @author : Carson
 */
func GetAdmin(username string, password string) (admin *entity.Admin, err error) {

	Site, _ := dao.GetSiteObject()
	ctx := context.TODO()
	query := Admin.Ctx(ctx)

	where := map[string]interface{}{}
	where["site_id"] = Site.Id
	where["username"] = username
	//where["password"], _ = helpers.Bcrypt(password)
	where["status"] = StatusOn
	where["delete_at"] = 0

	err = query.Where(where).Scan(&admin)
	if err != nil {
		return nil, err
	}
	if admin == nil {
		return nil, gerror.New("用户不存在")
	}

	ok := helpers.CompareBcrypt(admin.Password, password)
	if ok != true {
		return nil, gerror.New("密码不正确")
	}

	return
}
